import React, { createContext, useContext } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setResults } from '../slices/searchSlice';

// Create context
const SearchContext = createContext();

// Search provider component with Redux logic
export const SearchProvider = ({ children }) => {
  const dispatch = useDispatch();
  const searchResults = useSelector((state) => state.search.results);
  const comparisonProducts = useSelector((state) => state.search.comparisonProducts);

  const addToComparison = (product) => {
    // Check if product is already in comparison
    if (comparisonProducts.some(p => p.title === product.title && p.store === product.store)) {
      return;
    }

    // Add product to comparison (limit to 4 products)
    if (comparisonProducts.length < 4) {
      dispatch(setResults([...comparisonProducts, product]));
    }
  };

  return (
    <SearchContext.Provider value={{ searchResults, addToComparison }}>
      {children}
    </SearchContext.Provider>
  );
};

// Custom hook to use search context
export const useSearch = () => {
  return useContext(SearchContext);
};
